(function(global) {
    // Fonction constructeur pour créer un dé à 8 faces
    function Dice() {
      this.sides = 8;
    }
  
    // Méthode pour lancer le dé à 8 faces
    Dice.prototype.roll = function() {
      return Math.floor(Math.random() * this.sides) + 1;
    };
  
    // Exporter le module pour une utilisation globale
    global.Dice = Dice;
  })(window);
  